const readline=require("readline"),fs=require("fs"),random=require("random"),chalk=require("chalk");let games=1,trueAnswer=0,falseAnswer=0;fs.writeFileSync("./log.txt",`\nИгра ${games}`,"utf8");const rl=readline.createInterface({input:process.stdin,output:process.stdout});console.log("\nВыбери число "+chalk.black.bgYellow(" 1 ")+" или "+chalk.black.bgYellow.bold(" 2 ")),rl.on("line",inputDigit=>{const rnd=random.int(1,2);console.log("Ты выбрал:",inputDigit),console.log("Компьютер загадал:",rnd),inputDigit>2?(console.log(chalk.white.bgRed(" Ты выбрал число больше 2 ! Нужно выбрать число 1 или 2 ")),games--):inputDigit<1?(console.log(chalk.white.bgRed(" Ты выбрал число меньше 1 ! Нужно выбрать число 1 или 2 ")),games--):isNaN(inputDigit)?(console.log(chalk.white.bgRed(" Ты выбрал не число! Нужно выбрать число 1 или 2 ")),games--):inputDigit==rnd?(console.log(chalk.white.bgGreen(" Угадал! Молодец ")),trueAnswer++):inputDigit!==rnd&&(console.log(chalk.white.bgRed(" Не угадал! Попробуй еще раз ")),falseAnswer++),fs.writeFileSync("./log.txt",chalk.yellow(`--------------------\n  Всего попыток - ${games}\n  --Угадал: ${trueAnswer} раз.\n  --Неугадал: ${falseAnswer} раз.\n  --ИТОГ-- ${trueAnswer}:${falseAnswer}\n  --------------------`));var fileContent=fs.readFileSync("log.txt","utf8");console.log(fileContent),games++,console.log("\nПродолжим \nВыбери число "+chalk.black.bgYellow(" 1 ")+" или "+chalk.black.bgYellow(" 2 "))});